        [Fact]
        public void Shuffle_SmallArray_ReturnArrayCombinateValues() {
            int[] numsArray = new int[] { 2, 5, 1, 3, 4, 7 };
            int elements = 3;

            int[] outputArray = new int[] { 2, 3, 5, 4, 1, 7 };
            var result = _shuffleTheArray.Shuffle(numsArray, elements);
            Assert.Equal(outputArray, result);
        }
        [Fact]
        public void Shuffle_NegativeElement_ReturnArrayEmpty() {
            int[] numsArray = new int[] { 1, 2, 3, 4, 4, 3, 2, 1 };
            int elements = -1;

            var result = _shuffleTheArray.Shuffle(numsArray, elements);
            Assert.Empty(result);
        }
        [Fact]
        public void Shuffle_ArrayEmpty_ReturnSameArray() {
            int[] numsArray = new int[] { };
            int elements = 2;

            var result = _shuffleTheArray.Shuffle(numsArray, elements);
            Assert.Equal(numsArray,result);

        }
